import React, { useEffect, useState } from 'react';
import './BattlePage.css';
import battleImage from '../Assets/codebattle_Logo.jpg';

const CodingChallenge = () => {
    const [isLoading, setIsLoading] = useState(false);
    const [message, setMessage] = useState('');
    const [isWaiting, setIsWaiting] = useState(false); // Using state to manage isWaiting

    const handleSubmit = () => {
        setIsWaiting(true); // Setting isWaiting to true triggers a re-render
        sendMessageToServerlet();
    };

    const sendMessageToServerlet = async () => {
        const code = encodeURIComponent(document.getElementById('code-editor').value);
        try {
          const response = await fetch(`http://localhost:8080/LeetcodeBattleBackend/runCode?code=${code}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'text/plain'
            }
          });
    
          if (!response.ok) {
            throw new Error('Failed to send message to serverlet');
          }
          
          const responseData = await (await response.text()).trim;
          console.log(JSON.stringify(responseData))
          if (responseData === "Starting Battle...\n") {
            //window.location.href = '/battle?number=' + responeData;
          }
    
          
        } catch (error) {
          throw new Error('Error sending message to serverlet:', error);
        }
      };

    
    let requestSent = false;

    useEffect(() => {
        if (requestSent) return;

        const xhr = new XMLHttpRequest();
        xhr.open("POST", "http://localhost:8080/LeetcodeBattleBackend/runCode", true);
        xhr.setRequestHeader("Content-Type", "application/x-www-form-urlencoded");
        xhr.onreadystatechange = function () {
            if (xhr.readyState === XMLHttpRequest.DONE) {
                if (xhr.status === 200) {
                    console.log('Number sent to servlet successfully');
                } else {
                    console.error('Error sending number to servlet:', xhr.status);
                }
            }
        };
        xhr.send("number=" + number);
        requestSent = true;

    }, []); 

    return (
        <div className="container-main">
            <div className="left-panel">
                <div className="problem-description">
                    <h2>Problem Description</h2>
                    <p>Write a function that reverses a string. The input string is given as an array of characters s.</p>
                </div>
            </div>
            <div className="right-panel">
                <textarea
                    id="code-editor"
                    style={{ width: '100%', height: '250px', fontSize: '16px', padding: '10px' }}
                    defaultValue="solution(input, target)"
                ></textarea>
                <button style={{ width: '100px', margin: '10px', fontSize: '16px' }} onClick={handleSubmit}>Submit</button>
            </div>
            <div
                id="waiting"
                style={{
                    display: isWaiting ? 'block' : 'none',
                    position: 'fixed',
                    bottom: 100,
                    width: '100%',
                    textAlign: 'center',
                    marginLeft: 100,
                    padding: '10px',
                    color: 'white',
                    fontSize: '32px',
                    textColor: "white",
                }}
            >
                Waiting for opponent...
            </div>
        </div>
    );
};

export default CodingChallenge;
